const initialState={
    showCart: false
}

const showCartReducer =(state=initialState,action)=>{
    if (action.type==='SHOW_CART'){
        return {
            showCart: !state.showCart
        }
    }
    else {
        return state;
    }
}
export default showCartReducer;
