const initialState = {
    products : [],
    itemCount: 0
}

const productReducer = (state= initialState, action)=>{
    switch(action.type){
        case 'FETCH_PRODUCT':
            return {
                ...state,
                products: action.payload
            }
        case 'INCREASE':    
            return {
                ...state,
                itemCount: state.itemCount+1 
            }
        case 'DECREASE':    
            return {
                ...state,
                itemCount: state.itemCount-1 
            }


        default: 
            return state;
    }
}

export default productReducer;
