const initialState = {
    products : [],
    itemCount: 0,
    showCart: false,
    cart: []
}

const shopReducer = (state= initialState, action)=>{
    switch(action.type){
        case 'FETCH_PRODUCT':
            return {
                ...state,
                products: action.payload
            }
        case 'INCREASE':    
        console.log(action.payload?.stock)
            return {
                ...state,
                itemCount: action?.payload?.stock === state.itemCount ? state.itemCount :  state.itemCount + 1
            }
        case 'DECREASE':    
            return {
                ...state,
                itemCount: state.itemCount-1 
            }
        case 'SHOW_CART':

            return {
                ...state,
                showCart: !state.showCart
            }

        default: 
            return state;
    }
}

export default shopReducer;
